package qq;

public class SMenu {
	int no; //번호
	String name;//이름
	int price;//가격
	int amount;//갯수

	
	public SMenu() {
		// TODO Auto-generated constructor stub
	}
	
	public SMenu(int no, String name, int price, int amount) {
		super();
		this.no = no;
		this.name = name;
		this.price = price;
		this.amount = amount;
	}

}

